import React, { Component } from 'react'

export default class Parent extends Component {
    state={car:'奔驰'}
    render() {
        console.log('父亲的render')
        return (
            <div>
                父亲的车：{this.state.car}

                <Child car={this.state.car}/>
            </div>
        )
    }
}

class Child extends Component{
    render() {
        console.log('孩子的render')
        return (
            <div>
                孩子的车：{this.props.car}
            </div>
        )
    }
}
